elements.code:
// GLSL Shader
#ifdef GL_ES
precision mediump float;
#endif

uniform vec2 resolution;
uniform float time;

float squareSDF(vec2 p, float size) {
  vec2 d = abs(p) - vec2(size);
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/resolution.xy;
    uv -= 0.5;
    uv.x *= resolution.x / resolution.y; // Correct aspect ratio

    // Parameters
    float size = 0.3;
    float distortionFrequency = 2.0;
    float distortionAmplitude = 0.1;

    // Distort UVs
    uv += vec2(sin(uv.y * distortionFrequency + time), cos(uv.x * distortionFrequency + time)) * distortionAmplitude;

    // Square SDF
    float d = squareSDF(uv, size);

    // Color based on distance
    float color = smoothstep(0.01, 0.02, d);

    // Output to screen
    fragColor = vec4(color, color, color, 1.0);
}

void main()
{
	mainImage(gl_FragColor, gl_FragCoord.xy);
}

