elements.code:
//GLSL Shader Code
#version 460 core

layout (location = 0) out vec4 FragColor;

in vec2 UV;

uniform float u_time;
uniform vec2 u_resolution;

float squareSDF(vec2 p, float size)
{
    vec2 q = abs(p) - vec2(size);
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

void main()
{
    vec2 st = (UV * u_resolution - 0.5 * u_resolution)/u_resolution.y;

    float size = 0.2 + 0.1 * sin(u_time);

    float d = squareSDF(st,size);

    float color = smoothstep(0.01, 0.0, d);

    FragColor = vec4(color,color,color,1.0);
}

